
var mock = require('./lib');
var fs = require('fs');

mock({
  dir: {
    link: mock.symlink({
      path: 'foo'
    }),
    foo: {
      bar: 'content'
    }
  }
});

fs.readdir('dir', function(err, items) {
  if (err) {
    process.stderr.write(`${err.stack}\n`);
    process.exit(1);
  } else {
    process.stdout.write(`items: ${items}\n`);
    process.exit(0);
  }
});
